import * as React from 'react';
import { Avatar } from '../Avatar';
import { PlatformBadge } from '../PlatformBadge';
import { PriorityBadge } from '../PriorityBadge';
import type { Conversation } from '../../types/conversation';

interface ConversationItemProps {
  conversation: Conversation;
  onSelect: () => void;
}

export const ConversationItem: React.FC<ConversationItemProps> = ({ 
  conversation,
  onSelect 
}) => {
  return (
    <div className="conversation-item" onClick={onSelect}>
      <Avatar name={conversation.contact.name} />
      <div className="conversation-content">
        <div className="conversation-header">
          <div className="header-left">
            <h3>{conversation.contact.name}</h3>
            <PriorityBadge priority={conversation.priority} />
          </div>
          {conversation.unreadCount && (
            <span className="unread-badge">
              {conversation.unreadCount} new
            </span>
          )}
        </div>
        <p className="last-message">{conversation.lastMessage}</p>
        <div className="conversation-footer">
          <div className="platform-badges">
            {conversation.platforms.map((platform) => (
              <PlatformBadge 
                key={platform} 
                platform={platform} 
              />
            ))}
          </div>
          {conversation.contextualTags && (
            <div className="contextual-tags">
              {conversation.contextualTags.map((tag, index) => (
                <span key={index} className="context-tag">{tag}</span>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
