import * as React from 'react';
import { useState, useRef } from 'react';
import { AIFeatures } from '../AIFeatures';
import type { Conversation } from '../../types/conversation';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'contact';
  timestamp: Date;
  platform: string;
}

interface ConversationViewProps {
  conversation: Conversation | null;
}

export const ConversationView: React.FC<ConversationViewProps> = ({ conversation }) => {
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [messageText, setMessageText] = useState('');
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);

  const handleSendMessage = () => {
    if (!messageText.trim()) return;

    // Create a new message object
    const newMessage = {
      id: Date.now().toString(),
      text: messageText,
      sender: 'user' as const,
      time: new Date(),
      platform: conversation?.platforms[0] || 'WhatsApp', // Default to first platform
    };

    // Here you would typically:
    // 1. Add message to conversation
    // 2. Send to backend/API
    // 3. Update UI
    console.log('Sending message:', newMessage);

    // Clear input after sending
    setMessageText('');
  };

  const handleKeyPress = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
        const audioUrl = URL.createObjectURL(audioBlob);
        // Here you can handle the recorded audio
        console.log('Recording completed:', audioUrl);
        // You can send this audioBlob to your server or process it further
      };

      mediaRecorder.start();
      setIsRecording(true);
    } catch (error) {
      console.error('Error accessing microphone:', error);
      alert('Unable to access microphone. Please check permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
      setIsRecording(false);
    }
  };

  const handleVoiceButtonClick = () => {
    if (isRecording) {
      stopRecording();
    } else {
      startRecording();
    }
  };

  const handleSettingsClick = (action: string) => {
    switch (action) {
      case 'search':
        const searchText = prompt('Enter text to search in chat:');
        if (searchText) {
          alert(`Searching for: ${searchText} in chat with ${conversation?.contact.name}`);
        }
        break;
      case 'media':
        alert('Opening media gallery');
        break;
      case 'export':
        alert('Exporting chat history...');
        break;
      case 'clear':
        if (window.confirm('Are you sure you want to clear this chat? This cannot be undone.')) {
          alert('Chat cleared');
        }
        break;
      case 'block':
        if (window.confirm(`Are you sure you want to block ${conversation?.contact.name}?`)) {
          alert(`${conversation?.contact.name} has been blocked`);
        }
        break;
    }
    setIsSettingsOpen(false);
  };

  const settingsItems = [
    {
      label: 'Search in chat',
      action: 'search',
      color: '#0084ff'
    },
    {
      label: 'Media, links & docs',
      action: 'media',
      color: '#4CAF50'
    },
    {
      label: 'Export chat',
      action: 'export',
      color: '#9C27B0'
    },
    {
      label: 'Clear chat',
      action: 'clear',
      color: '#FF5722',
      isDangerous: true
    },
    {
      label: 'Block contact',
      action: 'block',
      color: '#d32f2f',
      isDangerous: true
    }
  ];

  if (!conversation) {
    return (
      <div className="conversation-placeholder">
        Select a conversation to start messaging
      </div>
    );
  }

  return (
    <div className="conversation-view">
      <div className="conversation-header">
        <div className="header-left">
          <h2>{conversation.contact.name}</h2>
          <div className="platform-badges">
            {conversation.platforms.map(platform => (
              <span 
                key={platform} 
                className="platform-badge"
                data-platform={platform}
              >
                {platform}
              </span>
            ))}
          </div>
        </div>
        <div className="header-actions">
          <div className="settings-menu">
            <button 
              className="settings-button"
              title="Settings"
              onClick={() => setIsSettingsOpen(!isSettingsOpen)}
            >
              <span role="img" aria-label="settings">⚙️</span>
            </button>
            {isSettingsOpen && (
              <div className="settings-dropdown">
                {settingsItems.map((item, index) => (
                  <button
                    key={index}
                    className={`settings-item ${item.isDangerous ? 'dangerous' : ''}`}
                    onClick={() => handleSettingsClick(item.action)}
                  >
                    <span className="settings-label" style={{ color: item.color }}>
                      {item.label}
                    </span>
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="conversation-content">
        <div className="message-history">
          {conversation.messages.map((message, index) => (
            <div 
              key={index} 
              className={`message-bubble ${message.platform.toLowerCase()}`}
            >
              <div className="message-content">
                <p>{message.text}</p>
                <div className="message-meta">
                  <span className="message-time">
                    {message.time.toLocaleTimeString([], { 
                      hour: '2-digit', 
                      minute: '2-digit',
                      hour12: true 
                    })}
                  </span>
                  <span 
                    className="platform-indicator"
                    data-platform={message.platform}
                  >
                    {message.platform}
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      <AIFeatures conversation={conversation} />

      <div className="message-input-container">
        <input 
          type="text" 
          className="message-input"
          placeholder="Type a message or use voice command..."
          value={messageText}
          onChange={(e) => setMessageText(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <button 
          className={`voice-button ${isRecording ? 'recording' : ''}`}
          title={isRecording ? 'Stop Recording' : 'Start Voice Recording'}
          onClick={handleVoiceButtonClick}
        >
          <span role="img" aria-label="voice">
            {isRecording ? '⏹️' : '🎤'}
          </span>
        </button>
        <button 
          className={`send-button ${messageText.trim() ? 'active' : ''}`}
          onClick={handleSendMessage}
          disabled={!messageText.trim()}
        >
          <span role="img" aria-label="send">📤</span>
        </button>
      </div>
    </div>
  );
};
