import * as React from 'react';
import { ConversationItem } from '../ConversationItem';
import { SettingsMenu } from '../SettingsMenu';
import type { Conversation, Platform, PriorityLevel } from '../../types/conversation';
import { useEffect, useState } from 'react';

interface MessageContent {
  text: string;
  platform: Platform;
  time: Date;
}

interface Message {
  id: string;
  sender: {
    name: string;
    initials: string;
  };
  content: string;
  priority: PriorityLevel;
  platforms: Platform[];
  time: Date;
  isNew?: boolean;
  messages?: MessageContent[];
}

interface MessageStats {
  totalMessages: number;
  highPriority: number;
  pendingReplies: number;
}

interface MessageListProps {
  onConversationSelect: (conversation: Conversation) => void;
  onStatsUpdate?: (stats: MessageStats) => void;
}

export const MessageList: React.FC<MessageListProps> = ({ 
  onConversationSelect, 
  onStatsUpdate 
}) => {
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);

  const messages: Message[] = [
    {
      id: '1',
      sender: {
        name: 'Alice Smith',
        initials: 'AS'
      },
      content: 'Can you review the Q1 report?',
      priority: 'High',
      platforms: ['WhatsApp' as Platform, 'Slack' as Platform],
      time: new Date('2024-02-20T10:00:00'),
      isNew: true,
      messages: [
        {
          text: 'Can you review the Q1 report?',
          platform: 'WhatsApp' as Platform,
          time: new Date('2024-02-20T10:00:00')
        },
        {
          text: "Of course! I'll take a look right away.",
          platform: 'WhatsApp' as Platform,
          time: new Date('2024-02-20T10:05:00')
        },
        {
          text: 'Great, particularly interested in the sales projections.',
          platform: 'Slack' as Platform,
          time: new Date('2024-02-20T10:07:00')
        }
      ]
    },
    {
      id: '2',
      sender: {
        name: 'Bob Johnson',
        initials: 'BJ'
      },
      content: 'Design files ready for review',
      priority: 'Medium',
      platforms: ['Matrix' as Platform, 'Email' as Platform],
      time: new Date('2024-02-20T11:30:00'),
      isNew: true,
      messages: [
        {
          text: 'Design files ready for review',
          platform: 'Matrix' as Platform,
          time: new Date('2024-02-20T11:30:00')
        },
        {
          text: 'UI components and layouts are ready for your feedback',
          platform: 'Email' as Platform,
          time: new Date('2024-02-20T11:32:00')
        }
      ]
    },
    {
      id: '3',
      sender: {
        name: 'Carol White',
        initials: 'CW'
      },
      content: 'Team meeting scheduled for tomorrow',
      priority: 'High',
      platforms: ['Slack' as Platform],
      time: new Date('2024-02-20T14:15:00'),
      messages: [
        {
          text: 'Team meeting scheduled for tomorrow at 10 AM',
          platform: 'Slack' as Platform,
          time: new Date('2024-02-20T14:15:00')
        }
      ]
    },
    {
      id: '4',
      sender: {
        name: 'David Brown',
        initials: 'DB'
      },
      content: 'Updated project timeline',
      priority: 'High',
      platforms: ['Email' as Platform, 'WhatsApp' as Platform],
      time: new Date('2024-02-20T15:45:00'),
      isNew: true,
      messages: [
        {
          text: 'Updated project timeline ready for review',
          platform: 'Email' as Platform,
          time: new Date('2024-02-20T15:45:00')
        },
        {
          text: 'Please check your email for the timeline updates',
          platform: 'WhatsApp' as Platform,
          time: new Date('2024-02-20T15:47:00')
        }
      ]
    },
    {
      id: '5',
      sender: {
        name: 'Emma Davis',
        initials: 'ED'
      },
      content: 'Client feedback on new features',
      priority: 'Medium',
      platforms: ['WhatsApp' as Platform, 'Slack' as Platform],
      time: new Date('2024-02-20T16:30:00'),
      messages: [
        {
          text: 'Initial feedback from client meeting',
          platform: 'WhatsApp' as Platform,
          time: new Date('2024-02-20T16:25:00')
        },
        {
          text: 'Client feedback on new features documented',
          platform: 'Slack' as Platform,
          time: new Date('2024-02-20T16:30:00')
        }
      ]
    }
  ];

  const handleMessageClick = (message: Message) => {
    const conversation: Conversation = {
      id: message.id,
      contact: {
        id: message.sender.initials.toLowerCase(),
        name: message.sender.name
      },
      lastMessage: message.content,
      platforms: message.platforms,
      unreadCount: message.isNew ? 1 : 0,
      priority: message.priority,
      messages: message.messages || [{
        text: message.content,
        platform: message.platforms[0],
        time: message.time
      }],
      aiSuggestions: [
        "I'll review it right away",
        'When do you need this by?',
        'Could you provide more details?'
      ],
      contextualTags: [],
      lastInteraction: message.time
    };

    onConversationSelect(conversation);
  };

  useEffect(() => {
    // Calculate stats from actual messages
    const stats = {
      totalMessages: messages.length,
      highPriority: messages.filter(msg => msg.priority === 'High').length,
      pendingReplies: messages.filter(msg => msg.isNew).length
    };
    onStatsUpdate?.(stats);
  }, [messages, onStatsUpdate]);

  const handleSettingsClick = (action: string) => {
    switch (action) {
      case 'search':
        const searchText = prompt('Enter text to search in conversations:');
        if (searchText) {
          alert(`Searching for: ${searchText} in all conversations`);
        }
        break;
      case 'media':
        alert('Opening media gallery');
        break;
      case 'wallpaper':
        alert('Opening wallpaper settings');
        break;
      case 'clear':
        if (window.confirm('Are you sure you want to clear all conversations? This cannot be undone.')) {
          alert('All conversations cleared');
        }
        break;
      case 'block':
        alert('Select a conversation to block a contact');
        break;
    }
    setIsSettingsOpen(false);
  };

  const settingsItems = [
    {
      icon: 'üîç',
      label: 'Search in conversations',
      action: () => handleSettingsClick('search'),
      color: '#1976D2'
    },
    {
      icon: 'üìé',
      label: 'Media, links and docs',
      action: () => handleSettingsClick('media'),
      color: '#388E3C'
    },
    {
      icon: 'üñºÔ∏è',
      label: 'Change wallpaper',
      action: () => handleSettingsClick('wallpaper'),
      color: '#7B1FA2'
    },
    {
      icon: 'üßπ',
      label: 'Clear all conversations',
      action: () => handleSettingsClick('clear'),
      color: '#E64A19',
      isDangerous: true
    }
  ];

  return (
    <div className="messages-container">
      <div className="messages-header">
        <div className="header-left">
          <h2>Messages</h2>
        </div>
        <div className="header-right">
          <button 
            className="voice-button" 
            title="Voice Message"
            onClick={() => alert('Select a conversation to send a voice message')}
          >
            <span role="img" aria-label="voice">üé§</span>
          </button>
          <div className={`settings-menu ${isSettingsOpen ? 'active' : ''}`}>
            <button 
              className="settings-button"
              title="Settings"
              onClick={() => setIsSettingsOpen(!isSettingsOpen)}
            >
              <span role="img" aria-label="settings">‚öôÔ∏è</span>
            </button>
            {isSettingsOpen && (
              <div className="settings-dropdown">
                {settingsItems.map((item, index) => (
                  <button
                    key={index}
                    className={`settings-item ${item.isDangerous ? 'dangerous' : ''}`}
                    onClick={item.action}
                    style={{ color: item.color }}
                  >
                    <span className="settings-icon">{item.icon}</span>
                    <span className="settings-label">{item.label}</span>
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {messages.length > 0 ? (
        <div className="messages-list">
          {messages.map(message => (
            <div 
              key={message.id} 
              className="message-item"
              onClick={() => handleMessageClick(message)}
            >
              <div className="message-item-content">
                <div className="avatar">{message.sender.initials}</div>
                <div className="message-details">
                  <div className="message-header">
                    <h3>{message.sender.name}</h3>
                    <div className="message-badges">
                      {message.isNew && <span className="new-badge">New</span>}
                      <span className={`priority-badge priority-${message.priority.toLowerCase()}`}>
                        {message.priority}
                      </span>
                    </div>
                  </div>
                  <p className="message-preview">{message.content}</p>
                  <div className="message-footer">
                    <div className="platform-badges">
                      {message.platforms.map(platform => (
                        <span 
                          key={platform} 
                          className="platform-badge"
                          data-platform={platform}
                        >
                          {platform}
                        </span>
                      ))}
                    </div>
                    <span className="message-time">
                      {message.time.toLocaleTimeString([], { 
                        hour: '2-digit', 
                        minute: '2-digit',
                        hour12: true 
                      })}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-messages">
          <p>No conversations yet</p>
        </div>
      )}
    </div>
  );
};
