import * as React from 'react';
import { AIFeatures } from '../AIFeatures';
import type { Conversation } from '../../types/conversation';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'contact';
  timestamp: Date;
  platform: string;
}

interface ConversationViewProps {
  conversation: Conversation | null;
}

export const ConversationView: React.FC<ConversationViewProps> = ({ conversation }) => {
  const messages = [
    {
      id: '1',
      text: 'Can you review the Q1 report?',
      sender: 'contact' as const,
      timestamp: new Date('2024-02-20T10:00:00'),
      platform: 'WhatsApp'
    },
    {
      id: '2',
      text: "Of course! I'll take a look right away.",
      sender: 'user' as const,
      timestamp: new Date('2024-02-20T10:05:00'),
      platform: 'WhatsApp'
    },
    {
      id: '3',
      text: 'Great, particularly interested in the sales projections.',
      sender: 'contact' as const,
      timestamp: new Date('2024-02-20T10:07:00'),
      platform: 'Slack'
    }
  ] as Message[];

  if (!conversation) {
    return (
      <div className="conversation-placeholder">
        Select a conversation to start messaging
      </div>
    );
  }

  return (
    <div className="conversation-view">
      <div className="conversation-header">
        <div className="contact-info">
          <h2>{conversation.contact.name}</h2>
          <div className="platform-badges">
            {conversation.platforms.map(platform => (
              <span key={platform} className={`platform-badge platform-${platform.toLowerCase()}`}>
                {platform}
              </span>
            ))}
          </div>
        </div>
        <div className="conversation-actions">
          <button className="action-button">
            <span role="img" aria-label="voice">üé§</span>
            Voice
          </button>
          <button className="action-button">
            <span role="img" aria-label="settings">‚öôÔ∏è</span>
            Settings
          </button>
        </div>
      </div>
      
      <div className="conversation-content">
        <div className="message-history">
          {messages.map(message => (
            <div 
              key={message.id} 
              className={`message ${message.sender === 'user' ? 'message-sent' : 'message-received'}`}
            >
              <div className="message-content">
                <p>{message.text}</p>
                <div className="message-meta">
                  <span className="message-time">
                    {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </span>
                  <span className="message-platform">{message.platform}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      <AIFeatures conversation={conversation} />
      
      <div className="message-input-container">
        <input 
          type="text" 
          className="message-input"
          placeholder="Type a message or use voice command..."
        />
        <button className="send-button">
          <span role="img" aria-label="send">üì§</span>
        </button>
      </div>
    </div>
  );
};
