import * as React from 'react';
import { jsPDF } from 'jspdf';

interface ExportOptionsProps {
  summaryData: {
    title: string;
    date: string;
    statistics: {
      totalMessages: number;
      highPriority: number;
      pendingReplies: number;
    };
    items: Array<{
      title: string;
      description: string;
      priority: string;
      platform: string;
      time: string;
    }>;
  };
}

export const ExportOptions: React.FC<ExportOptionsProps> = ({ summaryData }) => {
  const [showOptions, setShowOptions] = React.useState(false);

  const handleExportJSON = () => {
    const jsonString = JSON.stringify(summaryData, null, 2);
    downloadFile(jsonString, 'application/json', 'daily-summary.json');
  };

  const handleExportTXT = () => {
    const textContent = `
${summaryData.title}
Date: ${summaryData.date}

Statistics:
- Total Messages: ${summaryData.statistics.totalMessages}
- High Priority: ${summaryData.statistics.highPriority}
- Pending Replies: ${summaryData.statistics.pendingReplies}

Priority Items:
${summaryData.items.map(item => `
* ${item.title} (${item.priority})
  ${item.description}
  Platform: ${item.platform}
  Time: ${item.time}
`).join('\n')}
    `.trim();

    downloadFile(textContent, 'text/plain', 'daily-summary.txt');
  };

  const handleExportPDF = () => {
    const doc = new jsPDF();
    let yPos = 20;

    // Title
    doc.setFontSize(16);
    doc.text(summaryData.title, 20, yPos);
    yPos += 10;

    // Date
    doc.setFontSize(12);
    doc.text(`Date: ${summaryData.date}`, 20, yPos);
    yPos += 20;

    // Statistics
    doc.setFontSize(14);
    doc.text('Statistics:', 20, yPos);
    yPos += 10;
    doc.setFontSize(12);
    doc.text(`Total Messages: ${summaryData.statistics.totalMessages}`, 30, yPos);
    yPos += 7;
    doc.text(`High Priority: ${summaryData.statistics.highPriority}`, 30, yPos);
    yPos += 7;
    doc.text(`Pending Replies: ${summaryData.statistics.pendingReplies}`, 30, yPos);
    yPos += 20;

    // Priority Items
    doc.setFontSize(14);
    doc.text('Priority Items:', 20, yPos);
    yPos += 10;

    summaryData.items.forEach(item => {
      if (yPos > 250) {
        doc.addPage();
        yPos = 20;
      }

      doc.setFontSize(12);
      doc.text(`${item.title} (${item.priority})`, 30, yPos);
      yPos += 7;
      doc.setFontSize(10);
      doc.text(item.description, 30, yPos, { maxWidth: 150 });
      yPos += 7;
      doc.text(`Platform: ${item.platform}`, 30, yPos);
      yPos += 7;
      doc.text(`Time: ${item.time}`, 30, yPos);
      yPos += 15;
    });

    doc.save('daily-summary.pdf');
  };

  const handleEmailShare = () => {
    const subject = encodeURIComponent(summaryData.title);
    const body = encodeURIComponent(`
${summaryData.title}
Date: ${summaryData.date}

Statistics:
- Total Messages: ${summaryData.statistics.totalMessages}
- High Priority: ${summaryData.statistics.highPriority}
- Pending Replies: ${summaryData.statistics.pendingReplies}

Priority Items:
${summaryData.items.map(item => `
* ${item.title} (${item.priority})
  ${item.description}
  Platform: ${item.platform}
  Time: ${item.time}
`).join('\n')}
    `);

    window.location.href = `mailto:?subject=${subject}&body=${body}`;
  };

  const handleCopyLink = async () => {
    const summaryString = JSON.stringify(summaryData);
    const encodedData = btoa(summaryString);
    const shareUrl = `${window.location.origin}/share?data=${encodedData}`;
    
    try {
      await navigator.clipboard.writeText(shareUrl);
      alert('Share link copied to clipboard!');
    } catch (err) {
      console.error('Failed to copy link:', err);
    }
  };

  const downloadFile = (content: string, type: string, filename: string) => {
    const blob = new Blob([content], { type });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  return (
    <div className="export-options">
      <button 
        className="action-button"
        onClick={() => setShowOptions(!showOptions)}
      >
        <span role="img" aria-label="export">📥</span>
        Export Summary
      </button>
      
      {showOptions && (
        <div className="export-dropdown">
          <button onClick={handleExportJSON}>
            <span role="img" aria-label="json">📄</span>
            Export as JSON
          </button>
          <button onClick={handleExportTXT}>
            <span role="img" aria-label="text">📝</span>
            Export as Text
          </button>
          <button onClick={handleExportPDF}>
            <span role="img" aria-label="pdf">📑</span>
            Export as PDF
          </button>
          <button onClick={handleEmailShare}>
            <span role="img" aria-label="email">📧</span>
            Share via Email
          </button>
          <button onClick={handleCopyLink}>
            <span role="img" aria-label="link">🔗</span>
            Copy Share Link
          </button>
        </div>
      )}
    </div>
  );
};
