import * as React from 'react';
import { SearchBar } from '../SearchBar';
import { MessageList } from '../MessageList';
import { FilterButton } from '../FilterButton';
import { ConversationView } from '../ConversationView';
import { DailySummary } from '../DailySummary';
import type { Conversation } from '../../types/conversation';
import { Dashboard } from '../Dashboard';

interface MessageStats {
  totalMessages: number;
  highPriority: number;
  pendingReplies: number;
}

export const Inbox: React.FC = () => {
  const [activeTab, setActiveTab] = React.useState('inbox');
  const [selectedConversation, setSelectedConversation] = React.useState<Conversation | null>(null);
  const [messageStats, setMessageStats] = React.useState<MessageStats>({
    totalMessages: 5,
    highPriority: 3,
    pendingReplies: 3
  });

  const handleTabClick = (tab: string) => {
    setActiveTab(tab);
  };

  return (
    <div className="app-container">
      {/* Header */}
      <header className="app-header">
        <div className="header-left">
          <button className="menu-button">
            <span className="menu-icon">â˜°</span>
          </button>
          <h1 className="app-title">DailyFix</h1>
        </div>
        <div className="header-right">
          <button className="refresh-button">
            <span role="img" aria-label="refresh">ðŸ”„</span>
          </button>
          <button className="notifications-button">
            <span role="img" aria-label="notifications">ðŸ””</span>
          </button>
          <button className="profile-button">
            <span className="profile-icon">ðŸ‘¤</span>
          </button>
        </div>
      </header>

      {/* Navigation */}
      <nav className="app-nav">
        <button 
          onClick={() => handleTabClick('inbox')}
          className={`nav-link ${activeTab === 'inbox' ? 'active' : ''}`}
        >
          Inbox
        </button>
        <button 
          onClick={() => handleTabClick('dashboard')}
          className={`nav-link ${activeTab === 'dashboard' ? 'active' : ''}`}
        >
          Dashboard
        </button>
      </nav>

      {/* Main Content */}
      <main className="main-content">
        {activeTab === 'inbox' ? (
          <>
            <div className="inbox-container">
              <div className="inbox-header">
                <h2>Messages</h2>
                <FilterButton />
              </div>
              <SearchBar placeholder="Search messages..." />
              <DailySummary stats={messageStats} />
              <MessageList 
                onConversationSelect={setSelectedConversation}
                onStatsUpdate={setMessageStats}
              />
            </div>
            <ConversationView conversation={selectedConversation} />
          </>
        ) : (
          <Dashboard stats={messageStats} />
        )}
      </main>
    </div>
  );
};
