import * as React from 'react';
import { useState } from 'react';
import { AIFeatures } from '../AIFeatures';
import type { Conversation } from '../../types/conversation';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'contact';
  timestamp: Date;
  platform: string;
}

interface ConversationViewProps {
  conversation: Conversation | null;
}

interface ExportOption {
  icon: string;
  label: string;
  action: string;
  color: string;
}

export const ConversationView: React.FC<ConversationViewProps> = ({ conversation }) => {
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [currentOption, setCurrentOption] = useState<string | null>(null);

  const handleSettingsClick = (action: string) => {
    setCurrentOption(action);
    
    switch (action) {
      case 'search':
        const searchText = prompt('Enter text to search in chat:');
        if (searchText) {
          alert(`Searching for: ${searchText} in chat with ${conversation?.contact.name}`);
        }
        break;
      case 'media':
        alert('Opening media, links and docs');
        break;
      case 'wallpaper':
        alert('Opening wallpaper settings');
        break;
      case 'clear':
        if (window.confirm('Are you sure you want to clear this chat? This cannot be undone.')) {
          alert('Chat cleared');
        }
        break;
      case 'block':
        if (window.confirm(`Are you sure you want to block ${conversation?.contact.name}?`)) {
          alert(`${conversation?.contact.name} has been blocked`);
        }
        break;
    }
    
    setTimeout(() => {
      setCurrentOption(null);
      setIsSettingsOpen(false);
    }, 500);
  };

  const exportOptions: ExportOption[] = [
    {
      icon: '📊',
      label: 'Export Summary',
      action: 'summary',
      color: '#1976D2'
    },
    {
      icon: '📄',
      label: 'Export as JSON',
      action: 'json',
      color: '#388E3C'
    },
    {
      icon: '📝',
      label: 'Export as Text',
      action: 'text',
      color: '#7B1FA2'
    },
    {
      icon: '📑',
      label: 'Print as PDF',
      action: 'pdf',
      color: '#E64A19'
    },
    {
      icon: '📧',
      label: 'Share via Email',
      action: 'email',
      color: '#0288D1'
    },
    {
      icon: '🔗',
      label: 'Copy Share Link',
      action: 'link',
      color: '#455A64'
    }
  ];

  const handleExport = (action: string) => {
    switch (action) {
      case 'summary':
        alert('Exporting conversation summary...');
        break;
      case 'json':
        alert('Exporting as JSON...');
        break;
      case 'text':
        alert('Exporting as text...');
        break;
      case 'pdf':
        alert('Generating PDF...');
        break;
      case 'email':
        alert('Opening email client...');
        break;
      case 'link':
        alert('Share link copied to clipboard');
        break;
    }
  };

  if (!conversation) {
    return (
      <div className="conversation-placeholder">
        Select a conversation to start messaging
      </div>
    );
  }

  return (
    <div className="conversation-view">
      <div className="conversation-header">
        <div className="header-left">
          <h2>{conversation.contact.name}</h2>
          <div className="platform-badges">
            {conversation.platforms.map(platform => (
              <span 
                key={platform} 
                className="platform-badge"
                data-platform={platform}
              >
                {platform}
              </span>
            ))}
          </div>
        </div>
        <div className="header-actions">
          <button 
            className="voice-button" 
            title="Voice Message"
            onClick={() => alert('Voice recording started')}
          >
            <span role="img" aria-label="voice">🎤</span>
          </button>
          <div className="settings-options">
            {settingsItems.map((item, index) => (
              <button
                key={index}
                className={`settings-item ${item.isDangerous ? 'dangerous' : ''} ${currentOption === item.action ? 'active' : ''}`}
                onClick={() => handleSettingsClick(item.action)}
                style={{ color: item.color }}
                title={item.label}
              >
                <span className="settings-icon">{item.icon}</span>
              </button>
            ))}
          </div>
        </div>
      </div>
      
      <div className="conversation-content">
        <div className="message-history">
          {conversation.messages.map((message, index) => (
            <div 
              key={index} 
              className={`message-bubble ${message.platform.toLowerCase()}`}
            >
              <div className="message-content">
                <p>{message.text}</p>
                <div className="message-meta">
                  <span className="message-time">
                    {message.time.toLocaleTimeString([], { 
                      hour: '2-digit', 
                      minute: '2-digit',
                      hour12: true 
                    })}
                  </span>
                  <span 
                    className="platform-indicator"
                    data-platform={message.platform}
                  >
                    {message.platform}
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      <AIFeatures conversation={conversation} />
      
      <div className="message-input-container">
        <input 
          type="text" 
          className="message-input"
          placeholder="Type a message or use voice command..."
        />
        <button className="send-button">
          <span role="img" aria-label="send">📤</span>
        </button>
      </div>
    </div>
  );
};
