import * as React from 'react';
import { ConversationItem } from '../ConversationItem';
import type { Conversation, Platform, PriorityLevel } from '../../types/conversation';

interface MessageListProps {
  onConversationSelect: (conversation: Conversation) => void;
}

export const MessageList: React.FC<MessageListProps> = ({ onConversationSelect }) => {
  const conversations: Conversation[] = [
    {
      id: '1',
      contact: {
        id: 'as1',
        name: 'Alice Smith',
      },
      lastMessage: 'Can you review the Q1 report?',
      platforms: ['WhatsApp', 'Slack'] as Platform[],
      unreadCount: 3,
      priority: 'High' as PriorityLevel,
      aiSuggestions: [
        "I'll review it right away",
        'When do you need the review by?',
        'Could you highlight the key sections?'
      ],
      contextualTags: ['Q1 Report', 'Review'],
      lastInteraction: new Date(),
    },
    {
      id: '2',
      contact: {
        id: 'bj1',
        name: 'Bob Johnson',
      },
      lastMessage: 'Design files are ready for review',
      platforms: ['Matrix', 'Email'] as Platform[],
      unreadCount: 1,
      priority: 'Medium' as PriorityLevel,
      aiSuggestions: [
        "Thanks, I'll take a look",
        'Which design components should I focus on?',
        'Are there any specific areas you want feedback on?'
      ],
      contextualTags: ['Design', 'Review'],
      lastInteraction: new Date(),
    }
  ];

  return (
    <div className="message-list">
      {conversations.map(conversation => (
        <ConversationItem 
          key={conversation.id}
          conversation={conversation}
          onSelect={() => onConversationSelect(conversation)}
        />
      ))}
    </div>
  );
};
