import { useState, useRef, useEffect } from 'react';

interface ProfileUploadProps {
  onClose: () => void;
}

interface UserProfile {
  name: string;
  email: string;
  image: string | null;
}

const styles = `
.profile-upload-modal {
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  width: 300px;
  background: white;
  box-shadow: -2px 0 10px rgba(0,0,0,0.1);
  padding: 24px;
  z-index: 1000;
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.profile-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
