  background: #006699;
  transform: translateY(-1px);
}

.file-input {
  display: none;
}

.default-avatar {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #f0f0f0;
  font-size: 3rem;
  color: #666;
}

.profile-info {
  text-align: center;
  margin-top: 16px;
  width: 100%;
}

.profile-field {
  position: relative;
  margin-bottom: 16px;
  width: 100%;
}

.profile-input {
  width: 100%;
  padding: 8px 12px;
  border: 1px solid #ddd;
  border-radius: 8px;
  font-size: 0.9rem;
  transition: all 0.2s;
}

