import * as React from 'react';
import { ConversationItem } from '../ConversationItem';
import type { Conversation, Platform, PriorityLevel } from '../../types/conversation';
import { useEffect } from 'react';

interface Message {
  id: string;
  sender: {
    name: string;
    initials: string;
  };
  content: string;
  priority: 'High' | 'Medium' | 'Low';
  platforms: string[];
  time: Date;
  isNew?: boolean;
}

interface MessageStats {
  totalMessages: number;
  highPriority: number;
  pendingReplies: number;
}

interface MessageListProps {
  onConversationSelect: (conversation: Conversation) => void;
  onStatsUpdate?: (stats: MessageStats) => void;
}

export const MessageList: React.FC<MessageListProps> = ({ 
  onConversationSelect, 
  onStatsUpdate 
}) => {
  const messages: Message[] = [
    {
      id: '1',
      sender: {
        name: 'Alice Smith',
        initials: 'AS'
      },
      content: 'Can you review the Q1 report?',
      priority: 'High',
      platforms: ['WhatsApp'],
      time: new Date('2024-02-20T10:00:00'),
      isNew: true
    },
    {
      id: '2',
      sender: {
        name: 'Bob Johnson',
        initials: 'BJ'
      },
      content: 'Design files are ready for review',
      priority: 'Medium',
      platforms: ['Matrix', 'Email'],
      time: new Date('2024-02-20T11:30:00'),
      isNew: true
    },
    {
      id: '3',
      sender: {
        name: 'Carol White',
        initials: 'CW'
      },
      content: 'Team meeting scheduled for tomorrow',
      priority: 'High',
      platforms: ['Slack'],
      time: new Date('2024-02-20T14:15:00')
    },
    {
      id: '4',
      sender: {
        name: 'David Brown',
        initials: 'DB'
      },
      content: 'Updated project timeline ready for review',
      priority: 'High',
      platforms: ['Email'],
      time: new Date('2024-02-20T15:45:00'),
      isNew: true
    },
    {
      id: '5',
      sender: {
        name: 'Emma Davis',
        initials: 'ED'
      },
      content: 'Client feedback on new features',
      priority: 'Medium',
      platforms: ['Slack'],
      time: new Date('2024-02-20T16:30:00')
    }
  ];

  const handleMessageClick = (message: Message) => {
    // Convert Message to Conversation format
    const conversation: Conversation = {
      id: message.id,
      contact: {
        id: message.sender.initials.toLowerCase(),
        name: message.sender.name
      },
      lastMessage: message.content,
      platforms: message.platforms as Platform[],
      unreadCount: message.isNew ? 1 : 0,
      priority: message.priority as PriorityLevel,
      aiSuggestions: [
        "I'll review it right away",
        'When do you need this by?',
        'Could you provide more details?'
      ],
      contextualTags: [],
      lastInteraction: message.time
    };
    
    onConversationSelect(conversation);
  };

  useEffect(() => {
    // Calculate stats from actual messages
    const stats = {
      totalMessages: messages.length,
      highPriority: messages.filter(msg => msg.priority === 'High').length,
      pendingReplies: messages.filter(msg => msg.isNew).length
    };
    onStatsUpdate?.(stats);
  }, [messages, onStatsUpdate]);

  return (
    <div className="messages-container">
      {messages.map(message => (
        <div 
          key={message.id} 
          className="message-item"
          onClick={() => handleMessageClick(message)}
          style={{ cursor: 'pointer' }}
        >
          <div className="message-header">
            <div className="sender-info">
              <div className="avatar">{message.sender.initials}</div>
              <div className="sender-details">
                <h3>{message.sender.name}</h3>
                <span className="message-time">
                  {message.time.toLocaleTimeString([], { 
                    hour: '2-digit', 
                    minute: '2-digit',
                    hour12: true 
                  })}
                </span>
              </div>
            </div>
            <div className="message-badges">
              {message.isNew && <span className="new-badge">New</span>}
              <span className={`priority-badge priority-${message.priority.toLowerCase()}`}>
                {message.priority}
              </span>
            </div>
          </div>
          <p className="message-content">{message.content}</p>
          <div className="platform-badges">
            {message.platforms.map(platform => (
              <span 
                key={platform} 
                className="platform-badge"
                data-platform={platform}
              >
                {platform}
              </span>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};
