import * as React from 'react';
import { ConversationItem } from '../ConversationItem';
import type { Conversation, Platform, PriorityLevel } from '../../types/conversation';
import { useEffect, useState } from 'react';

interface MessageContent {
  id: string;
  text: string;
  platform: Platform;
  time: Date;
}

interface Message {
  id: string;
  sender: {
    name: string;
    initials: string;
    title?: string;
    priority: PriorityLevel;
  };
  content: string;
  priority: PriorityLevel;
  platforms: Platform[];
  time: Date;
  isNew?: boolean;
  unread?: boolean;
  isFromSender?: boolean;
  platformMessages?: {
    platform: Platform;
    messages: string[];
    lastActivity: Date;
    isFromSender: boolean;
    priority?: PriorityLevel;
  }[];
  messages?: MessageContent[];
}

interface MessageListProps {
  onConversationSelect: (conversation: Conversation) => void;
  onStatsUpdate: (stats: { totalMessages: number; highPriority: number; pendingReplies: number }) => void;
}

const styles = `
.message-item {
  display: flex;
  padding: 16px;
  border-bottom: 1px solid #e0e0e0;
  cursor: pointer;
  transition: background-color 0.2s;
  background: #fff;
}

.message-item:hover {
  background-color: #f8f9fa;
}

.message-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.message-header {
  display: flex;
  align-items: center;
  gap: 12px;
}

.contact-info {
  display: flex;
  align-items: center;
  gap: 12px;
  flex: 1;
}

.contact-avatar {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: #e9ecef;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 500;
  color: #495057;
}

.contact-details {
  display: flex;
  align-items: center;
  gap: 8px;
}

.contact-name {
  font-weight: 500;
  color: #212529;
  margin: 0;
}

.priority-badge {
  font-size: 0.75rem;
  padding: 2px 8px;
  border-radius: 12px;
  font-weight: 500;
}

.priority-badge.high {
  background-color: #ffe3e3;
  color: #e03131;
}

.priority-badge.medium {
  background-color: #fff3bf;
  color: #f08c00;
}

.priority-badge.low {
  background-color: #d3f9d8;
  color: #2f9e44;
}

.platform-badges {
  display: flex;
  gap: 6px;
  margin-left: auto;
}

.platform-badge {
  font-size: 0.75rem;
  padding: 3px 8px;
  border-radius: 12px;
}

.platform-badge[data-platform="WhatsApp"] {
  background-color: #e7f5e4;
  color: #25D366;
}

.platform-badge[data-platform="Slack"] {
  background-color: #f3e5f5;
  color: #4A154B;
}

.platform-badge[data-platform="Matrix"] {
  background-color: #e3f2fd;
  color: #0088CC;
}

.platform-badge[data-platform="Email"] {
  background-color: #fff3e0;
  color: #f57c00;
}

.message-meta {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.message-time {
  font-size: 0.75rem;
  color: #868e96;
}

.message-preview {
  color: #495057;
  font-size: 0.875rem;
  margin-left: 52px;  /* Aligns with the content after avatar */
}

.search-container {
  padding: 16px;
  background: #fff;
  border-bottom: 1px solid #e0e0e0;
  position: relative;
}

.search-input {
  width: 100%;
  padding: 8px 16px 8px 40px;
  border: 1px solid #e0e0e0;
  border-radius: 20px;
  font-size: 0.9rem;
  background-color: #f8f9fa;
  transition: all 0.2s;
}

.search-icon {
  position: absolute;
  left: 28px;
  top: 50%;
  transform: translateY(-50%);
  color: #6c757d;
  font-size: 1.1rem;
}

.search-input:focus {
  outline: none;
  background-color: #fff;
  border-color: #0088cc;
  box-shadow: 0 0 0 2px rgba(0,136,204,0.2);
}

.highlight {
  background-color: #fff3cd;
  padding: 2px;
  border-radius: 2px;
}

.message-item.search-match {
  background-color: #e8f4f8;
}
`;

export const MessageList: React.FC<MessageListProps> = ({ 
  onConversationSelect,
  onStatsUpdate
}) => {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      sender: { 
        name: 'Alice Smith', 
        initials: 'AS',
        title: 'Project Manager',
        priority: 'High'
      },
      content: 'Can you review the Q1 report?',
      priority: 'High',
      platforms: ['WhatsApp', 'Slack'],
      time: new Date('2024-02-20T10:00:00'),
      isNew: true,
      platformMessages: [
        {
          platform: 'WhatsApp' as Platform,
          messages: ['Can you review the Q1 report?'],
          isFromSender: true,
          lastActivity: new Date('2024-02-20T10:00:00')
        },
        {
          platform: 'WhatsApp' as Platform,
          messages: ["Of course! I will take a look right away."],
          isFromSender: false,
          lastActivity: new Date('2024-02-20T10:05:00')
        },
        {
          platform: 'Slack' as Platform,
          messages: ['Great, particularly interested in the sales projections.'],
          isFromSender: true,
          lastActivity: new Date('2024-02-20T10:07:00')
        }
      ]
    },
    {
      id: '2',
      sender: {
        name: 'Bob Johnson',
        initials: 'BJ',
        title: 'UI/UX Designer',
        priority: 'Medium'
      },
      content: 'Design files ready for review',
      priority: 'Medium',
