import * as React from 'react';
import { ConversationItem } from '../ConversationItem';
import type { Conversation, Platform, PriorityLevel } from '../../types/conversation';
import { useEffect, useState } from 'react';

interface MessageContent {
  text: string;
  platform: Platform;
  time: Date;
}

interface Message {
  id: string;
  sender: {
    name: string;
    initials: string;
    title: string;
    priority?: PriorityLevel;
  };
  content: string;
  priority: PriorityLevel;
  platforms: Platform[];
  time: Date;
  isNew?: boolean;
  messages?: MessageContent[];
  unread: boolean;
  platformMessages: {
    platform: Platform;
    messages: string[];
    lastActivity: Date;
    isFromSender?: boolean;
  }[];
}

interface MessageListProps {
  onConversationSelect: (conversation: Conversation) => void;
  onStatsUpdate: (stats: { totalMessages: number; highPriority: number; pendingReplies: number }) => void;
}

export const MessageList: React.FC<MessageListProps> = ({ 
  onConversationSelect,
  onStatsUpdate
}) => {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      sender: { 
        name: 'Alice Smith', 
        initials: 'AS',
        title: 'Project Manager - Q1 Review',
        priority: 'High'
      },
      content: 'Can you review the Q1 report?',
      priority: 'High',
      platforms: ['WhatsApp', 'Slack'],
      time: new Date('2024-02-20T10:30:00'),
      isNew: true,
      unread: true,
      platformMessages: [
        {
          platform: 'WhatsApp',
          messages: ['Can you review the Q1 report?'],
          lastActivity: new Date('2024-02-20T10:30:00')
        },
        {
          platform: 'Slack',
          messages: ['Q1 report needs review', 'Attached financial summary'],
          lastActivity: new Date('2024-02-20T10:35:00')
        }
      ]
    },
    {
      id: '2',
      sender: {
        name: 'Bob Johnson',
        initials: 'BJ',
        title: 'UI/UX Designer',
        priority: 'Medium'
      },
      content: 'Design files ready for review',
      priority: 'Medium',
      platforms: ['Matrix', 'Email'],
      time: new Date('2024-02-20T11:30:00'),
      isNew: true,
      unread: true,
      platformMessages: [
        {
          platform: 'Matrix',
          messages: ['Design files ready for review'],
          lastActivity: new Date('2024-02-20T11:30:00')
        },
        {
          platform: 'Email',
          messages: ['UI components and layouts are ready for your feedback'],
          lastActivity: new Date('2024-02-20T11:32:00')
        }
      ]
    },
    {
      id: '3',
      sender: {
        name: 'Carol White',
        initials: 'CW',
        title: 'Product Manager',
        priority: 'Medium'
      },
      content: 'Team meeting scheduled for tomorrow',
      priority: 'High',
      platforms: ['Slack'],
      time: new Date('2024-02-20T14:15:00'),
      unread: true,
      platformMessages: [
        {
          platform: 'Slack',
          messages: ['Team meeting scheduled for tomorrow at 10 AM'],
          lastActivity: new Date('2024-02-20T14:15:00'),
          isFromSender: true
        }
      ]
    },
    {
      id: '4',
      sender: {
        name: 'David Brown',
        initials: 'DB',
        title: 'Senior Developer',
        priority: 'High'
      },
      content: 'Updated project timeline',
      priority: 'High',
      platforms: ['Email', 'WhatsApp'],
      time: new Date('2024-02-20T15:45:00'),
      unread: true,
      platformMessages: [
        {
          platform: 'Email',
          messages: ['Updated project timeline ready for review'],
          lastActivity: new Date('2024-02-20T15:45:00'),
          isFromSender: true
        },
        {
          platform: 'WhatsApp',
          messages: ['Please check your email for the timeline updates'],
          lastActivity: new Date('2024-02-20T15:47:00'),
          isFromSender: true
        }
      ]
    },
    {
      id: '5',
      sender: {
        name: 'Emma Davis',
        initials: 'ED',
        title: 'UX Designer',
        priority: 'Low'
      },
      content: 'Client feedback on new features',
      priority: 'Medium',
      platforms: ['WhatsApp', 'Slack'],
      time: new Date('2024-02-20T16:30:00'),
      unread: false,
      platformMessages: [
        {
          platform: 'WhatsApp',
          messages: ['Initial feedback from client meeting'],
          lastActivity: new Date('2024-02-20T16:25:00'),
          isFromSender: true
        },
        {
          platform: 'Slack',
          messages: ['Client feedback on new features documented'],
          lastActivity: new Date('2024-02-20T16:30:00'),
          isFromSender: true
        }
      ]
    }
  ]);

  useEffect(() => {
    // Update stats whenever messages change
    const stats = {
      totalMessages: messages.length,
      highPriority: messages.filter(m => m.priority === 'High').length,
      pendingReplies: messages.filter(m => m.isNew).length
    };
    onStatsUpdate(stats);
  }, [messages, onStatsUpdate]);

  return (
    <div className="messages-list">
      <div className="messages-header">
        <h2>Messages</h2>
        <div className="header-actions">
          <select className="priority-filter">
            <option value="all">All Priority</option>
            <option value="high">High Priority</option>
            <option value="medium">Medium Priority</option>
            <option value="low">Low Priority</option>
          </select>
          <button className="filter-button">
            <span>⚡</span>
          </button>
        </div>
      </div>
      <div className="search-container">
        <input 
          type="search" 
          placeholder="Search messages..." 
          className="search-input"
        />
      </div>
      {messages.length > 0 ? (
        <div className="messages-container">
          {messages.map(message => (
            <div 
              key={message.id}
              className={`message-item ${message.unread ? 'unread' : ''} priority-${message.priority.toLowerCase()}`}
              onClick={() => onConversationSelect({
                id: message.id,
                contact: {
                  id: message.sender.initials.toLowerCase(),
                  name: message.sender.name
                },
                lastMessage: message.content,
                platforms: message.platforms,
                unreadCount: message.isNew ? 1 : 0,
                priority: message.priority,
                messages: message.messages || [],
                aiSuggestions: [],
                contextualTags: [],
                lastInteraction: message.time
              })}
            >
              <div className="avatar">
                {message.sender.initials}
              </div>
              <div className="message-content">
                <div className="message-header">
                  <div className="sender-info">
                    <h3>{message.sender.name}</h3>
                    {message.sender.priority && (
                      <span className={`priority-badge ${message.sender.priority.toLowerCase()}`}>
                        {message.sender.priority}
                      </span>
                    )}
                  </div>
                  <span className="message-time">
                    {message.time.toLocaleTimeString([], { 
                      hour: '2-digit', 
                      minute: '2-digit'
                    })}
                  </span>
                </div>
                <div className="message-subtitle">
                  {message.sender.title}
                </div>
                <div className="platform-badges">
                  {message.platforms.map(platform => (
                    <span 
                      key={platform}
                      className={`platform-badge ${platform.toLowerCase()}`}
                    >
                      {platform}
                    </span>
                  ))}
                </div>
                <div className="message-preview">
                  {message.content}
                </div>
                {message.unread && (
                  <span className="unread-indicator">unread</span>
                )}
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-messages">
          <p>No conversations yet</p>
        </div>
      )}
    </div>
  );
};
