import * as React from 'react';

interface SummaryItem {
  id: string;
  title: string;
  description: string;
  priority: 'High' | 'Medium' | 'Low';
  platform: string;
  time: Date;
}

interface DailySummaryProps {
  stats?: {
    totalMessages: number;
    highPriority: number;
    pendingReplies: number;
  };
}

export const DailySummary: React.FC<DailySummaryProps> = ({ 
  stats = {
    totalMessages: 24,
    highPriority: 8,
    pendingReplies: 12
  }
}) => {
  const summaryItems: SummaryItem[] = [
    {
      id: '1',
      title: 'Q1 Report Review',
      description: 'Review requested for Q1 financial report with focus on sales projections',
      priority: 'High',
      platform: 'WhatsApp',
      time: new Date('2024-02-20T10:00:00')
    },
    {
      id: '2',
      title: 'Design Files Review',
      description: 'New design files ready for review - UI components and layouts',
      priority: 'Medium',
      platform: 'Slack',
      time: new Date('2024-02-20T11:30:00')
    }
  ];

  return (
    <section className="summary-section">
      <div className="section-header">
        <span className="section-icon">ðŸ“‹</span>
        <h2>Daily Summary</h2>
      </div>

      <div className="summary-stats">
        <div className="stat-item">
          <label>Total Messages</label>
          <span className="stat-value">{stats.totalMessages}</span>
        </div>
        <div className="stat-item">
          <label>High Priority</label>
          <span className="stat-value high-priority">{stats.highPriority}</span>
        </div>
        <div className="stat-item">
          <label>Pending Replies</label>
          <span className="stat-value pending">{stats.pendingReplies}</span>
        </div>
      </div>

      <div className="summary-items">
        {summaryItems.map(item => (
          <div 
            key={item.id} 
            className={`summary-item priority-${item.priority.toLowerCase()}`}
          >
            <div className="summary-item-header">
              <h3>{item.title}</h3>
              <span className={`priority-badge priority-${item.priority.toLowerCase()}`}>
                {item.priority}
              </span>
            </div>
            <p className="summary-description">{item.description}</p>
            <div className="summary-meta">
              <span className="platform-badge">{item.platform}</span>
              <span className="time-badge">
                {item.time.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </span>
            </div>
          </div>
        ))}
      </div>

      <div className="summary-actions">
        <button className="action-button">
          <span role="img" aria-label="generate">ðŸ“Š</span>
          Generate Detailed Report
        </button>
        <button className="action-button">
          <span role="img" aria-label="export">ðŸ“¥</span>
          Export Summary
        </button>
      </div>
    </section>
  );
};
